import React, { useState ,useEffect } from "react"
import { Card, Button, Alert } from "react-bootstrap"
import { useAuth } from "./AuthContexte"
import { Link, useHistory } from "react-router-dom"
import { useQuery } from "urql";
import { client, ssrCache } from "../src/urqlClient";
import Graphs from "./graphh"
import { TypeChooser } from "react-stockcharts/lib/helper";
const TodosQuery = `
query {
  crypto {
    name
    time
		data{
    hcl{
      opentime
      Open
      High
      Close
    	Volume
      
    }
      formula{
        rsi
        rsiK
        rsiD
        aroonu
        aroond
        macd
        histogram
      }
      ai{
        bigmome{
          BUY2
          BUY1
          ambi
        }
        sell{
          amb0
          amb1
          amb2
          amb3
          amb99
        }
        smallmome{
          amo
          ci
        }
        buy{
          ambb
          ambb5
          ww6
          ww7
        }
        mome{
          amb14
          amb15
          amb13
          amb55
        }
        other{
          amo
          amo1
        }
      }
    }
  }
}
`;
export default function Dashboard() {
    const [result, reexecuteQuery] = useQuery({
        query: TodosQuery,
      });
      const { data, fetching, err } = result;

    
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth()
    const history = useHistory()

    async function handleLogout() {
        setError("")

        try {
            await logout()
            history.push("/auth")
        } catch {
            setError("Failed to log out")
        }
    }
    
    return (
        <>
            <Card>
                <Card.Body>
                    <h2 className="text-center mb-4">Profile</h2>
                    {error && <Alert variant="danger">{error}</Alert>}
                    <strong>Email:</strong> {currentUser.email}
                    <Link to="/update-profile" className="btn btn-primary w-100 mt-3">
                        Update Profile
          </Link>
                </Card.Body>
            </Card>
            <div className="w-100 text-center mt-2">
                <Button variant="link" onClick={handleLogout}>
                    Log Out
                    
        </Button>
      
        <div><pre>{!fetching ? data.crypto.map(data => 	
        <TypeChooser>
				{type => <Graphs type={type} data={data.data.hcl} />}
			</TypeChooser>)
       : "loading"}</pre></div>
   
            </div>
        </>
    )
}
