import React, { useState ,useEffect } from "react"
import { Card, Button, Alert } from "react-bootstrap"
import { useAuth } from "./AuthContexte"
import { Link, useHistory } from "react-router-dom"
import { useQuery } from "urql";
import { client, ssrCache } from "../src/urqlClient";
import Graphs from "./graphh"
import { TypeChooser } from "react-stockcharts/lib/helper";
import { useDetectAdBlock } from "adblock-detect-react";
import { AdBlockDetectedWrapper } from "adblock-detect-react";

 
const TodosQuery = `
query {
  crypto {
    name
    time
		data{
    hcl{
      opentime
      Open
      High
      Low
      Close
    	Volume
      
    }
      formula{
        rsi
        rsiK
        rsiD
        aroonu
        aroond
        macd
        histogram
      }
      ai{
        bigmome{
          BUY2
          BUY1
          ambi
        }
        sell{
          amb0
          amb1
          amb2
          amb3
          amb99
        }
        smallmome{
          amo
          ci
        }
        buy{
          ambb
          ambb5
          ww6
          ww7
        }
        mome{
          amb14
          amb15
          amb13
          amb55
        }
        other{
          amo
          amo1
        }
      }
    }
  }
}
`;
export default function Dashboard() {
 
  const adBlockDetected = useDetectAdBlock();
 
    const [result, reexecuteQuery] = useQuery({
        query: TodosQuery,
      });
      const { data, fetching, err } = result;
      
      const refresh = () => {
        // Refetch the query and skip the cache
        reexecuteQuery({ requestPolicy: 'cache-and-network' });
      };
    
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth()
    const history = useHistory()
    
    async function handleLogout() {
        setError("")

        try {
            await logout()
            history.push("/auth")
        } catch {
            setError("Failed to log out")
        }
    }
    useEffect(() => {
     
      const interval = setInterval(() => {
       
        refresh()

      }, 1000);
      return () => clearInterval(interval);
    }, [data]);
    return (
        <>
            <Card>
                <Card.Body>
                    <h2 className="text-center mb-4">{!fetching && data != null?  data.crypto.map(data => data.name + " "+data.time) : "Profile" }</h2>
                    {error && <Alert variant="danger">{error}</Alert>}
                    <strong>Email:</strong> {currentUser.email}
                    <Button variant="link" onClick={handleLogout}>
                    Log Out
                    
        </Button>
                </Card.Body>
            </Card>
            <div className="w-100 text-center mt-2">
            <AdBlockDetectedWrapper>
              <div>{"super us by disabling ad blocker on our website"}</div>
          </AdBlockDetectedWrapper>
      
            <div ><pre >{fetching || data == null ||adBlockDetected ?  "loading" :data.crypto.map(data => <Graphs key="{data}" data ={data} />) }</pre></div>
   
            </div>
        </>
    )
}
