import React, { useEffect ,useState } from 'react';

import PropTypes from "prop-types";

import { scaleTime } from "d3-scale";
import { utcDay } from "d3-time";

import { ChartCanvas, Chart } from "react-stockcharts";
import { CandlestickSeries } from "react-stockcharts/lib/series";
import { XAxis, YAxis } from "react-stockcharts/lib/axes";
import { fitWidth } from "react-stockcharts/lib/helper";
import { last, timeIntervalBarWidth } from "react-stockcharts/lib/utils";


function Graphh(props) {
    const [data, setData] = useState([]);
    const [loading ,setLoding] = useState(false)
    useEffect(()=>{
        loopData(props.data.data.hcl);
        
    },[loading])
    
    const loopData = (hcl) => {
        var array = [];
        var OpenLen = hcl.Open.length;
        
    
        for( var i = 0; i < OpenLen; i++){
            array.push({ opentime:new Date(props.data.data.hcl.opentime[i]),  open : hcl.Open[i], High: hcl.High[i], Low : hcl.Low[i],close: hcl.Close[i],Volume : hcl.Volume[i], })
        }
        setData(array)
        setLoding(true)
       
       
    }
    
    // console.log(new Date(props.data.data.hcl.opentime[0]))
    const xAccessor = (d) => d.opentime;
   
    const xExtents = [
        xAccessor(last(data)),
        xAccessor(data[data.length - 100])
    ];
    return (
        <div>
    <ChartCanvas width={1000} height={400}
        margin={{left: 50, right: 50, top:10, bottom: 30}} type={"SVG"}
        seriesName="MSFT"
        data={data}
        xAccessor={data => data.date} 
        xExtents={[new Date(2012, 0, 1), new Date(2012, 6, 2)]}>

    <Chart id={1} yExtents={d => [d.high, d.low]}>
        <XAxis axisAt="bottom" orient="bottom" ticks={6}/>
        <YAxis axisAt="left" orient="left" ticks={5} />
        <CandlestickSeries />
    </Chart>
</ChartCanvas>
        </div>
    )
}

export default Graphh
