import React, { Component } from 'react'
import { Form, Button, Card, Alert } from "react-bootstrap"
import { Link, useHistory } from "react-router-dom"
import "./Home.css"
export default class Signup extends Component {




    constructor({ gun }) {
        super();
        this.state = { loading: false, error: "" };
        this.emailRef = React.createRef();
        this.passwordRef = React.createRef();
        this.passwordConfirmRef = React.createRef();
        this.gun = gun
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    handleSubmit(e) {
        e.preventDefault();


        if (this.passwordRef.current.value !== this.passwordConfirmRef.current.value) {
            this.setState({ error: "Passwords do not match" })


        } else {
            setLoading(false)
            console.log(this.emailRef.current.value)
            console.log(this.passwordRef.current.value)
            console.log(this.passwordConfirmRef.current.value)

            //var user = props.gun.user();
            //user.create(emailRef.current.value, passwordRef.current.value)
            window.location.reload(false);
        }
    }

    render() {
        return (
            <>
                <Card className="main" style={{ "borderRadius": "20px" }}>
                    <Card.Body>
                        <h2 className="text-center mb-4">Sign Up</h2>
                        {this.state.error && <Alert variant="danger">{this.state.error}</Alert>}
                        <Form onSubmit={handleSubmit}>
                            <Form.Group id="email">
                                <Form.Label>Email</Form.Label>
                                <Form.Control type="email" ref={this.emailRef} required />
                            </Form.Group>
                            <Form.Group id="password">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" ref={this.passwordRef} required />
                            </Form.Group>
                            <Form.Group id="password-confirm">
                                <Form.Label>Password Confirmation</Form.Label>
                                <Form.Control type="password" ref={this.passwordConfirmRef} required />
                            </Form.Group>
                            <Button disabled={this.state.loading} className="w-100" type="submit">
                                Sign Up
            </Button>
                        </Form>
                    </Card.Body>
                </Card>

            </>
        )
    }
}
