import React, { Component } from 'react'

import { useRef, useState } from "react"
import { Form, Button, Card, Alert } from "react-bootstrap"
import { Link, useHistory } from "react-router-dom"
import Gun from 'gun'

export default class Signin extends Component {
    constructor() {
        super();
        this.state = { emailRef: null, passwordRef: null, passwordConfirmRef: null, error: "", loading: false };
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    handleSubmit(e) {
        e.preventDefault()

        if (this.state.passwordRef !== this.state.passwordConfirmRef) {
            return this.setState({ error: "Passwords do not match" })
        }

        this.setState({ loading: false })
    }
    render() {
        return (
            <>

                <Card className="main" style={{ "borderRadius": "20px" }}>
                    <Card.Body>
                        <h2 className="text-center mb-4">Log In</h2>
                        {this.state.error && <Alert variant="danger">{this.state.error}</Alert>}
                        <Form onSubmit={this.handleSubmit}>
                            <Form.Group id="email">
                                <Form.Label>Email</Form.Label>
                                <Form.Control type="email" ref={this.state.emailRef} required />
                            </Form.Group>
                            <Form.Group id="password">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" ref={this.state.passwordRef} required />
                            </Form.Group>
                            <Button disabled={this.state.loading} className="w-100" type="submit">
                                Log In
                      </Button>
                        </Form>

                    </Card.Body>
                </Card>

            </>
        )
    }
}
