import React, { useEffect ,useState } from 'react';

import PropTypes from "prop-types";
import './graphh.css';
import { format } from "d3-format";
import {
    OHLCTooltip,
	SingleValueTooltip,
    
} from "react-stockcharts/lib/tooltip";
import { scaleTime } from "d3-scale";
import { utcDay } from "d3-time";
import { utcHour  } from "d3-time";
import { ChartCanvas, Chart } from "react-stockcharts";
import { CandlestickSeries ,LineSeries } from "react-stockcharts/lib/series";
import { XAxis, YAxis } from "react-stockcharts/lib/axes";
import { fitWidth } from "react-stockcharts/lib/helper";
import { last, timeIntervalBarWidth } from "react-stockcharts/lib/utils";
import { withRouter } from 'react-router-dom';
import { timeFormat } from "d3-time-format";
import {

    Link
  } from "react-router-dom";
import {
    CrossHairCursor,
    EdgeIndicator,
    CurrentCoordinate,
    MouseCoordinateX,
    MouseCoordinateY
} from "react-stockcharts/lib/coordinates";
import {
    Label,
    Annotate,
    SvgPathAnnotation,
    buyPath,
    sellPath,
  } from "react-stockcharts/lib/annotation";

function Graphh(props) {
    const  [display1, setDisplay1] = useState();
    const  [display2, setDisplay2] = useState();
    const  [display3, setDisplay3] = useState();
    const  [display4, setDisplay4] = useState();
    const  [display5, setDisplay5] = useState();
    const  [display6, setDisplay6] = useState();
    const  [display7, setDisplay7] = useState();
    const  [display8, setDisplay8] = useState();
    const  [display9, setDisplay9] = useState();
    const  [display10, setDisplay10] = useState();
    const  [display11, setDisplay11] = useState();
    const  [display12, setDisplay12] = useState();
    const  [display13, setDisplay13] = useState();
    const  [display14, setDisplay14] = useState();
    const  [display15, setDisplay15] = useState();
 
    const [data, setData] = useState([]);
    const [width,setWidth] =useState()
    
    const [loading ,setLoding] = useState(false)
    const [price ,setPrice] = useState(0)
   
     
   
    useEffect(()=>{
        loopData(props.data.data.hcl);
       
    },[props.data])
    
    const loopData = (hcl) => {
        
        var array = [];
        var OpenLen = hcl.Open.length;
        //props.data.data.
        for( var i = 0; i < OpenLen; i++){
            
            array.push({ date: new Date(hcl.opentime[i]),  open : hcl.Open[i], high: hcl.High[i], low : hcl.Low[i],close: hcl.Close[i],volume : hcl.Volume[i],buy2: props.data.data.ai.bigmome.BUY2[i],ambi: props.data.data.ai.bigmome.ambi[i]  ,amb0: props.data.data.ai.sell.amb0[i],amb1: props.data.data.ai.sell.amb1[i],amb2: props.data.data.ai.sell.amb2[i],amb3: props.data.data.ai.sell.amb3[i],amb99: props.data.data.ai.sell.amb99[i],amb13: props.data.data.ai.mome.amb13[i],amb14: props.data.data.ai.mome.amb14[i],
                amb15: props.data.data.ai.mome.amb15[i],amb55: props.data.data.ai.mome.amb55[i],ci: props.data.data.ai.smallmome.ci[i],
                rsik: props.data.data.formula.rsiK[i],macd: props.data.data.formula.macd[i],histogram: props.data.data.formula.histogram[i],ambb5: props.data.data.ai.buy.ambb5[i] ,BUYSELL :props.data.data.ai.other.BUYSELL[i] })
        }
        setData(array)
        setWidth(window.innerWidth)
        setLoding(true)
        setPrice(props.data.data.hcl.Close[0])
       
        
    }
    
    const longAnnotationProps = {
		
			y: ({ yScale, datum }) => yScale(datum.low),
			fill: "#006517",
			path: buyPath,
			tooltip: "Go long",
		};
    const shortAnnotationProps = {
			y: ({ yScale, datum }) => yScale(datum.high),
			fill: "#FF0000",
			path: sellPath,
			tooltip: "Go short",
		};
    const margin = { left: 70, right: 70, top: 20, bottom: 30 };

    const height = 400;
    const gridHeight = height - margin.top - margin.bottom;
    const gridWidth = width - margin.left - margin.right;
    const showGrid = true;
    const yGrid = showGrid
    ? { innerTickSize: -1 * gridWidth, tickStrokeOpacity: 0.2 }
    : {};
  const xGrid = showGrid
    ? {  innerTickSize: -2 * gridHeight,
      tickStrokeDasharray: 'Solid',
      tickStrokeOpacity: 0.2,
      tickStrokeWidth: 1 }
    : {};

    window.addEventListener("resize", function() {
        setWidth(window.innerWidth)
        
    });
    window.addEventListener("resize", function() {
      setWidth(window.innerWidth)
  });
    // console.log(new Date(props.data.data.hcl.opentime[0]))
  
    const xAccessor = (d) => {
        if (d != null){
          
            return d.date
        }
      }
  
    const xExtents = [
        xAccessor(last(data)),
        xAccessor(data[data.length - 150])
    ];

    return (
     

        <div  style={{
          height:"105vh",
          maxHeight: '50%',
       
          
        }}  >
       	   {loading && data!==null ? <ChartCanvas  className ="aa"   height={945}
