				<Chart height={150} id={0} yExtents={d => [d.high, d.low]}
                 padding={{ top: 10, bottom: 10 }}>
                 
        
					<YAxis axisAt="right" orient="right" ticks={7}  {...yGrid}/>
             <Annotate with={SvgPathAnnotation} when={d => d.BUYSELL === 1}
						usingProps={longAnnotationProps} />
            <Annotate with={SvgPathAnnotation} when={d => d.BUYSELL === 2}
						usingProps={shortAnnotationProps} />   
       	<XAxis  showGrid={true} axisAt="bottom" orient="bottom"
           opacity={2}
           showTicks={false}/>
			  
                    
					<CandlestickSeries width={timeIntervalBarWidth(utcHour.every(4))}/>
