				</Chart>
                
     
        <Chart
          id={1}
          yExtents={d =>  d.ambi }
          height={75}
  
          origin={(w, h) => [0, h - 740 ]}
          padding={{ top: 10, bottom: 10 }}
          
        >
         
          <YAxis axisAt="right" orient="right" ticks={3} {...yGrid} />
    
