                                   <CurrentCoordinate
           yAccessor={d =>  d.ambb5}  fill= { !display15 ? "#283747":"#FFFFFF"}
          />
          
            <XAxis axisAt="bottom" orient="bottom" 	outerTickSize={10}         {...xGrid} />
            <MouseCoordinateX
            at="bottom"
            orient="bottom"
            opacity={2}
            displayFormat={timeFormat("%Y-%m-%d")}
          />
          <MouseCoordinateY
            at="right"
            orient="right"
            displayFormat={format(".2f")}
          />
        </Chart>
          <CrossHairCursor />
			</ChartCanvas> : ""}
        </div>
    )
