import React, { useEffect ,useState } from 'react';

import PropTypes from "prop-types";

import { scaleTime } from "d3-scale";
import { utcDay } from "d3-time";

import { ChartCanvas, Chart } from "react-stockcharts";
import { CandlestickSeries } from "react-stockcharts/lib/series";
import { XAxis, YAxis } from "react-stockcharts/lib/axes";
import { fitWidth } from "react-stockcharts/lib/helper";
import { last, timeIntervalBarWidth } from "react-stockcharts/lib/utils";


function Graphh(props) {
    const [data, setData] = useState([]);
    const [loading ,setLoding] = useState(false)
    useEffect(()=>{
        loopData(props.data.data.hcl);
        
    },[loading])
    
    const loopData = (hcl) => {
        var array = [];
        var OpenLen = hcl.Open.length;
        
       
        for( var i = 0; i < OpenLen; i++){
            array.push({ date:props.data.data.hcl.opentime[i],  open : hcl.Open[i], high: hcl.High[i], low : hcl.Low[i],close: hcl.Close[i],volume : hcl.Volume[i], })
        }
        setData(array)
        setLoding(true)
       
       
    }
    
    // console.log(new Date(props.data.data.hcl.opentime[0]))
    const xAccessor = (d) => console.log(d.date);
    
    const xExtents = [
        xAccessor(last(data)),
        xAccessor(data[data.length - 100])
    ];

    return (
        <div>
       	   {!loading ? <ChartCanvas height={400}
					ratio={1000}
					width={800}
					margin={{ left: 50, right: 50, top: 10, bottom: 50 }}
					type={"svg"}
					seriesName="MSFT"
					data={data}
					xAccessor={xAccessor (data)}
					xScale={scaleTime()}
					xExtents={xExtents}>

				<Chart id={0} yExtents={d => [d.high, d.low]}>
					<XAxis axisAt="bottom" orient="bottom" ticks={2}/>
					<YAxis axisAt="left" orient="left" ticks={5} />
					<CandlestickSeries width={timeIntervalBarWidth(utcDay)}/>
				</Chart>
			</ChartCanvas> : "loading"}
        </div>
    )
}

export default Graphh
