import React, { useEffect ,useState } from 'react';

import PropTypes from "prop-types";

import { scaleTime } from "d3-scale";
import { utcDay } from "d3-time";
import { utcHour  } from "d3-time";
import { ChartCanvas, Chart } from "react-stockcharts";
import { CandlestickSeries ,LineSeries } from "react-stockcharts/lib/series";
import { XAxis, YAxis } from "react-stockcharts/lib/axes";
import { fitWidth } from "react-stockcharts/lib/helper";
import { last, timeIntervalBarWidth } from "react-stockcharts/lib/utils";


function Graphh(props) {
    const [data, setData] = useState([]);
  
    const [loading ,setLoding] = useState(false)
    useEffect(()=>{
        loopData(props.data.data.hcl);
        
    },[loading])
    
    const loopData = (hcl) => {
        console.log(props.data.data)
        var array = [];
        var OpenLen = hcl.Open.length;
        //props.data.data.
        for( var i = 0; i < OpenLen; i++){
            
            array.push({ date: new Date(hcl.opentime[i]),  open : hcl.Open[i], high: hcl.High[i], low : hcl.Low[i],close: hcl.Close[i],volume : hcl.Volume[i], buy1: props.data.data.ai.bigmome.BUY1[i] , buy2: props.data.data.ai.bigmome.BUY2[i],ambi: props.data.data.ai.bigmome.ambi[i]   })
        }
        setData(array)
        setLoding(true)
        
    }
    
    // console.log(new Date(props.data.data.hcl.opentime[0]))
  
    const xAccessor = (d) => {
        if (d != null){
          
            return d.date
        }
      }
  
    const xExtents = [
        xAccessor(last(data)),
        xAccessor(data[data.length - 300])
    ];

    return (
        <div>
       	   {loading ?<ChartCanvas  height={600}
					ratio={50}
					width={800}
					margin={{ left: 50, right: 50, top: 20, bottom: 30 }}
					type={"svg"}
					seriesName="MSFT"
					data={data}
					xAccessor={d =>  d.date}
					xScale={scaleTime()}
					xExtents={xExtents}>

				<Chart height={300} id={0} yExtents={d => [d.high, d.low]}>
				
					<YAxis axisAt="left" orient="left" ticks={5} />
                    
					<CandlestickSeries width={timeIntervalBarWidth(utcHour.every(4))}/>
                    <XAxis
            axisAt="bottom"
            orient="bottom"
            showTicks={false}
            outerTickSize={0}
          />
				</Chart>
                
                <Chart
          id={1}
          yExtents={d =>  d.buy2 }
          height={150}
          origin={(w, h) => [0, h - 250 ]}
          padding={{ top: 10, bottom: 10 }}
        >
          <XAxis axisAt="bottom" orient="bottom" />
          <YAxis axisAt="right" orient="right" ticks={2} />

          <LineSeries yAccessor={d =>  d.buy2} stroke = {"#283747"}  />
          <LineSeries yAccessor={d =>  d.ambi} stroke = {"#88B04B"}  />
          <LineSeries yAccessor={d =>  d.buy1} stroke = {"#FFA500"} />
         
        </Chart>
			</ChartCanvas> : "loading"}
        </div>
    )
}

export default Graphh
