        query: TodosQuery,
      })
      const { data, fetching, err } = result;
      
     
    
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth()
    const history = useHistory()
    
    async function handleLogout() {
        setError("")

        try {
            await logout()
            history.push("/auth")
        } catch {
            setError("Failed to log out")
        }
    }
    useEffect(() => {
      const intervalId = setInterval(() => {
        client.reexecuteOperation(result.operation)
        console.log(data)
      }, 1000)
      return () => clearInterval(intervalId);
    }, [result.operation, client]);
    return (
        <>
            <Card>
                <Card.Body>
                    <h2 className="text-center mb-4">{!fetching ? data.crypto.map(data => data.name + " "+data.time) : "Profile" }</h2>
                    {error && <Alert variant="danger">{error}</Alert>}
                    <strong>Email:</strong> {currentUser.email}
                    <Button variant="link" onClick={handleLogout}>
                    Log Out
                    
        </Button>
                </Card.Body>
            </Card>
            <div className="w-100 text-center mt-2">
             
      
        <div ><pre >{fetching ?  "loading" :data.crypto.map(data =>  <Graphs key="{data}"data ={data} />) }</pre></div>
   
            </div>
        </>
    )
}
