import React from 'react'
import Signup from "./Signup"
import Signin from "./Signin"
import { Link, useHistory } from "react-router-dom"
import sample from './images/production ID_4990241.mp4';
import "./Home.css"
export default function Home() {
    const [showResults, setShowResults] = React.useState(false)
    const onClick = () => {
        if (showResults == false)
            setShowResults(true);
        else {
            setShowResults(false);
        }
    }
    return (

        <div >

            <video className="myVideo" autoPlay loop muted >
                <source src={sample} type='video/mp4' />
            </video>
            <div class="wrap">
                <div class="glitch" style={{ position: "relative", color: "white" }} data-text="GLITCH">GLITCH</div>
            </div>
            <div className="d-flex align-items-center justify-content-center"
                style={{ minHeight: "100vh" }} >
                <div className="w-100" style={{ maxWidth: "400px" }}>
                    <div className="main">
                        {!showResults ? <Signin /> : null}
                        <div className="w-100 text-center mt-2" style={showResults ? { display: 'none' } : { position: "relative", color: "white" }} >
                            Need an account? <Link onClick={onClick} >Sign Up</Link>
                        </div>
                    </div>
                    <div>
                        {showResults ? <Signup /> : null}
                        <div className="w-100 text-center mt-2" style={showResults ? { position: "relative", color: "white" } : { display: 'none' }}>
                            Already have an account? <Link onClick={onClick} >Log In</Link>
                        </div>
                    </div>
                </div>
            </div>
        </div >
    )
}
