import React, { useEffect ,useState } from 'react';

import PropTypes from "prop-types";

import { scaleTime } from "d3-scale";
import { utcDay } from "d3-time";
import { utcHour  } from "d3-time";
import { ChartCanvas, Chart } from "react-stockcharts";
import { CandlestickSeries } from "react-stockcharts/lib/series";
import { XAxis, YAxis } from "react-stockcharts/lib/axes";
import { fitWidth } from "react-stockcharts/lib/helper";
import { last, timeIntervalBarWidth } from "react-stockcharts/lib/utils";


function Graphh(props) {
    const [data, setData] = useState([]);
  
    const [loading ,setLoding] = useState(false)
    useEffect(()=>{
        loopData(props.data.data.hcl);
        
    },[loading])
    
    const loopData = (hcl) => {
        var array = [];
        var OpenLen = hcl.Open.length;
        
       
        for( var i = 0; i < OpenLen; i++){
            array.push({ date: new Date(props.data.data.hcl.opentime[i]),  open : hcl.Open[i], high: hcl.High[i], low : hcl.Low[i],close: hcl.Close[i],volume : hcl.Volume[i], })
        }
        setData(array)
        setLoding(true)
     
       
    }
    
    // console.log(new Date(props.data.data.hcl.opentime[0]))
  
    function xAccessor(d) {
        if (d != null){
    
            return d.date
        }
      }
    const xExtents = [
        xAccessor(last(data)),
        xAccessor(data[data.length - 300])
    ];

    return (
        <div>
       	   {loading ?			<ChartCanvas height={400}
					ratio={10}
					width={800}
					margin={{ left: 50, right: 50, top: 10, bottom: 30 }}
					type={"svg"}
					seriesName="MSFT"
					data={data}
					xAccessor={xAccessor}
					xScale={scaleTime()}
					xExtents={xExtents}>

				<Chart id={1} yExtents={d => [d.high, d.low]}>
				
					<YAxis axisAt="left" orient="left" ticks={5} />
                    <XAxis axisAt="bottom" orient="bottom" ticks={6}/>
					<CandlestickSeries width={timeIntervalBarWidth(utcHour.every(4))}/>
				</Chart>
                
			</ChartCanvas> : "loading"}
        </div>
    )
}

export default Graphh
